function OnUpdate(doc, meta) {
    /* Check if further analysis is needed */
    if (doc.type !== "transaction") return;
    var card = register["card:" + doc.card];
    if (card.threshold > doc.amount) return;

    /* Schedule analysis at night (but for workshop: 60s later) */
    review[doc.txnid] = {"txnid": doc.txnid, "status": "open", "reason": "threshold"};
    var tm = new Date();
    tm.setSeconds(tm.getSeconds() + 15);
    createTimer(ExtendedVerify, tm, doc.txnid, doc);
}

function ExtendedVerify(doc) {
    /* Check if user has transacted with this merchant before */
    var tcard = doc.card;
    var score = 0;
    var reasons = {"product": false, "merchant": false, "proximity": false};

    var history =
        SELECT txnid, product, merchant, city
        FROM register
        WHERE
            type = "transaction" AND 
            card = $tcard;

    for (var txn of history) {
        if (txn.txnid === doc.txnid) {
            continue;
        }
        /* Check past purchases of this product */
        if (doc.product === txn.product) {
            score += 8;
            reasons["product"] = true;
        }
        /* Check interaction with this merchant */
        if (doc.merchant === txn.merchant) {
            score += 5;
            reasons["merchant"] = true;
        }
        /* Check locality to city and county */
        if (doc.city.name === txn.city.name) {
            score += 2;
            reasons["proximity"] = true;
        }
        if (doc.city.county === txn.city.county) {
            score += 1;
            reasons["proximity"] = true;
        }
    }

    var status = {"txnid": doc.txnid, "score": score};
    for (var key in reasons) {
        if (!reasons[key]) {
            status[key] = "suspected";
        } else {
            status[key] = "expected";
        }
    }

    if (score > 300) {
        status["disposition"] = ["closed"];
    }
    else if (score > 100) {
        status["disposition"] = ["human-review"];
    }
    else {
        status["disposition"] = ["sms-alert", "human-review"];
        log("Suspicious transaction: " + doc.txnid)
    }

    review[doc.txnid] = status;
}
