// This function runs at the promised delivery date to see status of package
function statusCheck(context) {
    var label = labels_bucket[context];
    if (label.delivered) return;
    sendEmail('someone@example.com', // Put in a valid email address here
              label.number,
              label.exceptions);
}

// This function executes whenever a label is created or modified
function OnUpdate(label, meta) {
    if (label.delivered) return; // ignore packages that are already delivered
    var deliver_by = new Date(label.deliver_by);
    log("Delayed package, sending email: ", label.number);
    createTimer(statusCheck,     // function to run when timer fires
                deliver_by,      // date for timer to fire
                meta.id,         // unique key set to document key
                label.number);   // user context set to label number
}

// This function sends an email with details of the delay
function sendEmail(to, reference, reasons) {
    var msg = 'We are sorry, your package ' + reference + ' is delayed due to following reasons:<p>';
    for (var i = 0; reasons && i < reasons.length; i++) {
        msg += 'Exception: [' + reasons[i].date + '] - ' + reasons[i].description + '<br>';
    }
    msg += '<p>Please call us at 1-800-555-1212 if you need further information';
    var email = {
        'personalizations': [{'to': [{'email': to}]}],
        'from': {'email': 'cb.ci.bot@gmail.com'},
        'subject': 'Package delivery delay: ' + reference,
        'content': [{'type': 'text/html', value: msg}]
    };
    var response = curl('POST', mailer_binding, {body: email});
    if (response.status < 200 || response.status >= 300) {
        log("Failed to send email: ", response);
    }
}
